import React, {useState} from 'react';
import HeaderItem from "./HeaderItem";
import MainIcon from "../Re-usable/Main Icon/MainIcon";
import {clinicsURLs as clinicsURL, employeeURLS, patientURLS, waitingQueueURLs} from "../../Constants/URLConstants";
import {useRouteMatch} from "react-router";
import {useReducer} from "react/cjs/react.production.min";

const Header = () => {

    // const [hideClass, setHideClass] = useState('hide');

    const[navbar,toggleNavbar]=useReducer((state)=>{
    return navbar==='hide'?'':'hide'
    },'')

 

    return (
        <header className="header-wrapper">
            <div className={'header-setting'}>
                <MainIcon/>
                <span className="arrow-button"
                      onClick={() => {
                          toggleNavbar()
                      }}/>
            </div>


            <div className={`header-links ${hideClass}`}>
                <HeaderItem path={'/dashboard'} icon={'dashboard'} link={"Dashboard"} m={  toggleNavbar()
}/>
                <HeaderItem path={`${patientURLS.patient}/${patientURLS.patientForm}`} icon={'hospital-patient'} link={"Patient"} m={  toggleNavbar()
}/>
                <HeaderItem path={`${employeeURLS.employee}/${employeeURLS.employeeForm}`} icon={'employee'} link={"Employee"} m={  toggleNavbar()
}/>
                <HeaderItem path={`${clinicsURL.clinic}/${clinicsURL.clinics}`} icon={'clinics'} link={"Clinics"} m={  toggleNavbar()
}/>
                <HeaderItem path={`${waitingQueueURLs.waitingQueue}/${waitingQueueURLs.waitingQueueList}`} icon={'waitingQueue'} link={"Waiting Queue"} m={  toggleNavbar()
}/>
            </div>
        </header>
    );
};

export default Header;
