import React, {Suspense, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";

const Employee = (props) => {
    const [component, setComponent] = useState(null);

    const LoadComponent = location => {
        const Component = React.lazy(() => import(`./Employee Forms/${location}`));
        setComponent(<Component/>);
    };

    useEffect(() => {
        // to load component from props

        props.name === undefined ?
            LoadComponent("EmployeeForm") :
            LoadComponent(props.name)

    },[props.name])

    return (

        <div className="patient-wrapper">
            <div className="patient-component-buttons">

                <NavLink type="button" className="btn  btn-primary"
                         onClick={() => LoadComponent("EmployeeForm")} to={'#'}>
                    Create Patient
                </NavLink>
                <NavLink type="button" className="btn  btn-primary"
                         onClick={() => LoadComponent("FindEmployee")} to={'#'}>
                    Find Patient
                </NavLink>
                <NavLink type="button" className="btn  btn-primary"
                         onClick={() => LoadComponent("EmployeeListTable")}   to={'#'}>
                    Patients List
                </NavLink>


            </div>

            <div className="employee-components">
                <Suspense fallback={<div>Loading...</div>}>{component}</Suspense>
            </div>


        </div>


    )

}

export default Employee;
