import React, {useState} from 'react';
import {Link} from "react-router-dom";
import {getDateFromTimeStamp} from "../../../../Common Functions/PersonFunctions";

const PatientInvoices = ({invoices}) => {

    const [chargedTotal,setChargedTotal] =useState(0)
    const [payedTotal,setPayedTotal] =useState(0)
    const [dueTotal,setDueTotal] =useState(0)

    const increment=(charged,payed,due)=>{
        setChargedTotal(charged+chargedTotal)
        setPayedTotal(payed+payedTotal)
        setDueTotal(due+dueTotal)
    }

    return (
        <div className="patient-invoices-wrapper">

            {invoices.length > 0 ?
                <div className="patient-invoices">

                    <table className="table table-striped">
                        <thead>
                        <tr>
                            <th>id</th>
                            <th>Charged</th>
                            <th>Payed</th>
                            <th>Due Amount</th>
                            <th>Issued At</th>
                            <th>Treatment</th>
                            <th>Edit</th>

                        </tr>
                        </thead>
                        <tbody>
                        {
                            invoices.map(invoice => {

                                increment(invoice.charged,invoice.amountPayed,invoice.dueAmount)

                                return <tr key={invoice.id}>
                                    <td>{invoice.id}</td>
                                    <td>{invoice.charged}</td>
                                    <td>{invoice.amountPayed}</td>
                                    <td>{invoice.dueAmount}</td>
                                    <td>{getDateFromTimeStamp(invoice.issuedAt)}</td>
                                    <td>{invoice.treatment}</td>
                                    <td><Link to={"#"}>More</Link></td>
                                </tr>
                            })
                        }
                        </tbody>
                    </table>
                </div>
                : "no"}
        </div>
    );
};

export default PatientInvoices;
