import React, {useState} from 'react';

import {useForm} from "react-hook-form";
import TextInputCustom from "../../Re-usable/FormComponents/TextInputCustom";

import SelectCustom from "../../Re-usable/FormComponents/SelectCustom";
import NumberInputCustom from "../../Re-usable/FormComponents/NumberInputCustom";
import {useHistory} from "react-router";


const PatientForm = (props) => {
// only the form here

    const patientId = props.match !== undefined ? props.match.params.id : null
    const {register, handleSubmit, reset, errors} = useForm();
    const [loading, setLoading] = useState(false);
    const [serverError, setServerError] = useState()//if there is server errors returned stored here
    const history = useHistory();
    const [added, setAdded] = useState(null);


    return (
        <div className="patient-form">

            <h1 className="form-heading">Patient Form</h1>

            {loading === false && <div>Loading user info . . . .</div>}
            {serverError && <p> {serverError.message}</p>}
        
            <h1 className="added">   {added === null ? null : "Employee " + added + " information saved successfully.."} </h1>
            <hr/>
            {patientId !== null ?
                <button className="btn-danger" onClick={() => deletePatientMethod(patientId)}>delete</button>
                : null}
        </div>
    );
};

export default PatientForm;
