import React, {useLayoutEffect, useState} from 'react';

import PersonCard from "../../../Re-usable/PersonCard";
import {getPatientsList} from "../Patient Logic/PatientFunctions";
import Pagination from "../../../Re-usable/Pagination/Pagination";


function PatientListTable({rowClick, modifyPatientMethod}) {


    const [patient, setPatient] = useState([])
    const [currentPage, setCurrentPage] = useState(0)
    const [totalPages, setTotalPages] = useState(1)
    const [itemsPerPage, setItemsPerPage] = useState(10)

    const paginate = (pageNumber) => {
        console.log('pagination called')
        setCurrentPage(pageNumber)
        getPatientsList(setPatient, setTotalPages, currentPage, itemsPerPage)

    }

    useLayoutEffect(() => {
        // this function set the patients array
        getPatientsList(setPatient, setTotalPages, 0, itemsPerPage)
    }, [])


    return (

        <div className=" cards-table-wrapper">

            {
                patient.length > 0 ?
                    patient.map((patient, index) => {
                        return <PersonCard key={index} person={patient}/>
                    })

                    : "no patients found"
            }

            <Pagination totalPages={totalPages} current={currentPage} paginate={paginate}/>

        </div>

    );
}

export default PatientListTable;
