import React from 'react';
import {Link} from "react-router-dom";
import {getFullName, getPersonAge} from "../../Common Functions/PersonFunctions";


// used to show information about one use only


const PersonCard = ({person}) => {
    const {
        person: {personName: name, mobileNumber, village, dateOfBirth},

    } = person;


    // console.log("person", person);
    const icon = person.gender === true ? 'female' : 'male';
    return (
        <div className={'person-card-wrapper'}>
            <div className="icon-wrapper">
                <img src={process.env.PUBLIC_URL + `/assets/icons/female.svg`} alt={`gender icon`}/>
                {/*<img src={process.env.PUBLIC_URL+`/assets/icons/${icon}.svg`} alt={`gender icon`}/>*/}
            </div>
            <div className="main">
                <h2 className="full-name">{getFullName(name)}</h2>
                <h5 className="mobile-number">{mobileNumber}</h5>
                <h5 className="last-visit">2 days ago</h5>
            </div>

            <div className="secondary">
                <h3 className="person-age">{getPersonAge(dateOfBirth)}</h3>
                <h3 className="village">{village}</h3>
                <Link className={'more'} to={'#'}>More</Link>

            </div>
        </div>);
};

export default PersonCard;
