import React, {Suspense, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";

const Patient = (props) => {
    const [component, setComponent] = useState(null);

    const LoadComponent = location => {
        const Component = React.lazy(() => import(`./${location}`));
        setComponent(<Component/>);
    };

    useEffect(() => {
        console.log(props.name)
        props.name === undefined ?
            LoadComponent("PatientForm") :
            LoadComponent(props.name)

    }, [])

    return (

        <div className="patient-wrapper">
            <div className="patient-component-buttons">
               
                <NavLink type="button" className="btn  btn-primary"
                         onClick={() => LoadComponent("PatientForm")} to={'#'}>
                    Create Patient
                </NavLink>
                <NavLink type="button" className="btn  btn-primary"
                         onClick={() => LoadComponent("FindPatient")} to={'#'}>
                    Find Patient
                </NavLink>
                <NavLink type="button" className="btn  btn-primary"
                         onClick={() => LoadComponent("PatientListTable")} to={'#'}>
                    Patients List
                </NavLink>


            </div>

            <div className="patient-components">
                <Suspense fallback={<div>Loading...</div>}>{component}</Suspense>
            </div>


        </div>


    )

}

export default Patient;
