import React, {useLayoutEffect, useState} from 'react';
import ClinicForm from "./ClinicForm";
import ClinicsTable from "./ClinicsTable";
import {getAllClinicsService} from "./Clinics Functions/ClinicsServices";
import api from "../../Re-usable/Api Url/apiURL";
import {webApiURL as webApiUrl} from "../../../Constants/URLConstants";

const Clinic = (props) => {
    let clinicId = props.match !== undefined ? props.match.params.id : undefined
    const [update, setUpdate] = useState(true)
    const [clinics, setClinics] = useState([]);
    const [clinic, setClinic] = useState([]);

    const getClinic = async () => {
        try {
            const res = await api.get(webApiUrl.clinics.getClinic + clinicId);
            setClinic(res.data)
        } catch (e) {

        }
    }
    const getAllClinics = async () => {
        setClinics(await getAllClinicsService());
        if (clinicId !== undefined) {//if the id is found in the url
            await getClinic()
        }
    }

    useLayoutEffect(() => {
        let mounted = true

        if (mounted)
            getAllClinics()

        return () => {
            mounted = false
        }
    }, [update, props.match])


    return (
        <div className="clinics-wrapper">
            <ClinicForm clinic={clinicId === undefined ? undefined : clinic}/>
            <ClinicsTable clinics={clinics}/>

        </div>
    );
};

export default Clinic;
