import React, {useLayoutEffect, useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {useHistory} from "react-router";
import {deleteClinic, getAllClinics, modifyClinic} from "../../Actions/ClinicActions";
import TableWithModify from "../Common Components/TableWithModify";

const ClinicsTable = () => {

    const dispatch = useDispatch();
    const clinics = useSelector(state => state.clinic.clinics)
    const history = useHistory()
    const [loading,setLoading] =useState(false)
    useLayoutEffect(() => {
        dispatch(getAllClinics(history))
    }, [])
    useLayoutEffect(() => {
setLoading(!loading)
    }, [clinics])

    function editClinic(id) {
        dispatch(modifyClinic(id, history))
    }

    function deleteClinicMethod(id) {
        dispatch(deleteClinic(id));
    }

    return (
        <div className="">
            <TableWithModify title="Clinic List">
                <thead>
                <tr>
                    <th>#</th>
                    <th>Clinic</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                </thead>
                <tbody>
                {clinics.map((clinic, index) => <tr key={index}>
                    <td>{++index}</td>
                    <td>{clinic.clinicName}</td>
                    <td>
                        <button className="btn-secondary"
                                onClick={() => editClinic(clinic.id)}>edit
                        </button>
                    </td>
                    <td>
                        <button className="btn-danger"
                                onClick={() => deleteClinicMethod(clinic.id)}>delete
                        </button>
                    </td>
                </tr>)
                }
                </tbody>
            </TableWithModify>
        </div>
    );
};

export default ClinicsTable;
