import React, {useEffect, useLayoutEffect, useState} from 'react';
import {useForm} from "react-hook-form";
 import {postClinic} from "./Clinics Functions/ClinicsFunctions";
import TextInputCustom from "../../Re-usable/FormComponents/TextInputCustom";
import {messages, registerObject} from "../../../Common Functions/FormObjects";
import api from "../../Re-usable/Api Url/apiURL";
import {webApiURL as webApiUrl} from "../../../Constants/URLConstants";

function ClinicForm({setUpdate, clinic}) {

    const {handleSubmit, errors, reset, register} = useForm(clinic!==undefined?{ defaultValues:{"clinicName":clinic.clinicName+""}}:{});


    useEffect(()=>{
        console.log("--clinic form update")
    })


    const getClinic = async (clinicId, setClinic) => {
        try {
            const res = await api.get(webApiUrl.clinics.getClinic + clinicId);
            setClinic(res.data)
            reset({clinicName:clinic.clinicName})

            // console.log("clinic response ", res.data)
        } catch (e) {

        }
    }

//     useLayoutEffect( () => {
// const fetchData=async () => {
//     if (clinicId !== null) {//if the id is found in the url
//         setLoading(false)
//
//         await  getClinic(clinicId, setClinic)
//         // console.log("clinic from fetch",clinic)
//         setLoading(true)
//     } else
//         setLoading(true)
//
// }
//  fetchData()
//
//     }, [    ])
//

    function onSubmit(data, e) {
        data.clinicName=data.clinicName.trim();
         if (clinic.id !== undefined)
            data = {...data, id: clinic.id }

        postClinic(data).then(() => {
            // e.target.reset();
            setUpdate()
        });
    }


// console.log("Clinic",clinic)
    return (
        <div className=" clinic-form-container">
            {clinic === undefined ? null
                :
                <div>
                    <h4>edit clinic </h4>
                    <h5>previous name : {clinic.clinicName}</h5>
                </div>}
            <>
                <h1 className="mainTitle">Add Clinic</h1>
                <p className="font-weight-bold">Choose a unique clinic name</p>
                <form onSubmit={handleSubmit(onSubmit)} >
                    <TextInputCustom field="clinicName" label="Clinic Name" errorMessages={messages}
                                     form={{register, errors, registerObject}}/>
                    <input type="submit"/>
                </form>
            </>

        </div>

    );
}

export default ClinicForm;
