import React, {useState} from 'react';
import HeaderItem from "./HeaderItem";
import MainIcon from "../Re-usable/Main Icon/MainIcon";
import {clinicsURLs as clinicsURL, patientURLS, waitingQueueURLs} from "../../Constants/URLConstants";

const Header = () => {

    const [hideClass, setHideClass] = useState('hide');

    function showHideHeaderLinks() {
        hideClass === 'hide' ? setHideClass('') : setHideClass('hide');
    }

    return (
        <header className="header-wrapper">
            <div className={'header-setting'}>
                <MainIcon/>
                <span className="arrow-button"
                      onClick={() => {
                          showHideHeaderLinks()
                      }}/>
            </div>


            <div className={`header-links ${hideClass}`}>
                <HeaderItem path={'/dashboard'} icon={'dashboard'} link={"Dashboard"} m={showHideHeaderLinks}/>
                <HeaderItem path={`/${patientURLS.patient}/form`} icon={'hospital-patient'} link={"Patient"} m={showHideHeaderLinks}/>
                <HeaderItem path={'/employee/form'} icon={'employee'} link={"Employee"} m={showHideHeaderLinks}/>
                <HeaderItem path={`/${clinicsURL.clinic}/${clinicsURL.clinics}`} icon={'clinics'} link={"Clinics"} m={showHideHeaderLinks}/>
                <HeaderItem path={`${waitingQueueURLs.waitingQueue}/${waitingQueueURLs.waitingQueueList}`} icon={'waitingQueue'} link={"Waiting Queue"} m={showHideHeaderLinks}/>
            </div>
        </header>
    );
};

export default Header;
