import React, {useEffect, useLayoutEffect, useState} from 'react';
 import ClinicForm from "./ClinicForm";
 import ClinicsTable from "./ClinicsTable";
import {getAllClinics} from "./Clinics Functions/ClinicsFunctions";
import api from "../../Re-usable/Api Url/apiURL";
import {webApiURL as webApiUrl} from "../../../Constants/URLConstants";

const Clinic = (props) => {


    const clinicId = props.match !== undefined ? props.match.params.id : null
    const [upadate,setUpdate]=useState(true)
    const [clinics, setClinics] = useState([]);
    const [clinic, setClinic] = useState([]);


    useEffect(()=>{
        console.log("--clinic update")
    })

    function refresh() {
        setUpdate(!upadate)
    }

    const getClinic = async ( ) => {
        try {
            const res = await api.get(webApiUrl.clinics.getClinic + clinicId);
            setClinic(res.data)


            // console.log("clinic response ", res.data)
        } catch (e) {

        }
    }
    const fetchData=async () => {

         console.log("--clinic table,,,,, effect")
    setClinics(await getAllClinics()) ;
        if (clinicId !== null) {//if the id is found in the url
            setLoading(false)

            await  getClinic()
            // console.log("clinic from fetch",clinic)
            setLoading(true)
        } else
            setLoading(true)

        console.log('clinic data from the clinic',clinics)

    }
     useLayoutEffect(() => {


        fetchData()

    }, [upadate])



    return (
        <div className="clinics-wrapper">

            <ClinicForm  setUpdate={refresh}/>
            <ClinicsTable clinics={clinics}  />

        </div>
    );
};

export default Clinic;
