import {frontToBackEmployee} from "../../../../Common Functions/PersonFunctions";
import {employeeURLS, webApiURL} from "../../../../Constants/URLConstants";
import axios from 'axios';
import api from '../../../Re-usable/Api Url/apiURL'

export const getEmployee = async (setEmployee, employeeId) => {

    try {
        const result = await api.get(employeeURLS.getEmployee + `/${employeeId}`);

        console.log("m", result.data)
        setEmployee(result.data)
    } catch (e) {

    }


}


export const getEmployeeInformation = async (setEmployee, setInvoices, employeeId) => {


    const requestOne = api.get(webApiURL.employee.getEmployee + `/${employeeId}`);
    // const requestTwo = api.get(employeeURLS.getEmployeeInvoices + `/${employeeId}`);

    await axios.all([requestOne]).then(axios.spread((...responses) => {
        const responseOne = responses[0]
        console.log("employee", responseOne.data)
        setEmployee(responseOne.data)
    })).catch(e => {
        console.log(e)
    })
}


export const postEmployee = async (employee, id, setServerError, setAdded, e) => {

    setServerError("");
    setAdded(false)

    employee = frontToBackEmployee(employee);
    if (id !== null) {
        employee = {...employee, id: id}
    }
    try {
        const result = await api.post(webApiURL.employee.postEmployee, employee);


        if (result.status === 201) {
            setAdded(true)
            setTimeout(function () {
                setAdded(false)
            }, 5000);
        }

        e.target.reset();
    } catch (e) {
        console.log(e.response.data)

        //TODO handle validation errors
        setServerError(e.response.data);
    }

}

export const submitEmployee = (employee) => {


    postEmployee(employee).then(value => console.log('employee submitted'));
}


export const getEmployeesList = async (pageNumber, itemsPerPage) => {
    console.log("employee list ")
    try {
        const res = await api.get(`/employee/all?page=${pageNumber}&size=${itemsPerPage}`)
        return res.data

    } catch (e) {
        console.log(e.response)
    }

}


export const checkEmployeeDept = (dueAmount) => {
    return dueAmount > 0 ? "hasDept" : ""
}

export const findEmployeesForm = async (name, setEmployeesList, setServerError) => {//method to get the employees list based on the person name if there is any error it is stored in the --> serverError
    console.log(name)
    try {
        const res = await api.get(webApiURL.employee.findEmployeeByName + name)
        setEmployeesList(res.data)
    } catch (e) {
        if (e.response)
            setServerError(e.response.data)
        setEmployeesList([])
    }
}
