import api from '../../../Re-usable/Api Url/apiURL'
import {frontToBackPatient} from "../../../../Common Functions/PersonFunctions";
import {patientURLS} from "../../../../Constants/URLConstants";
import axios from 'axios';

export const getPatient = async (setPatient, patientId) => {

    try {
        const result = await api.get(patientURLS.getPatient + `/${patientId}`);

        console.log("m", result.data)
        setPatient(result.data)
    } catch (e) {

    }


}


export const getPatientInformation = async ( patientId) => {
    // const patient = api.get(patientURLS.getPatient + `/${patientId}`);
    // const invoices = api.get(patientURLS.getPatientInvoices + `/${patientId}`);
    //
    // await api.all([patient, invoices]).then(api.spread((...response) => {
    //     const patientResponse = response[0];
    //     const invoicesResponse = response[1];
    //
    //     console.log("patient",patientResponse)
    //     console.log("invoices",invoicesResponse)
    // })).catch(e =>console.log(e))
    let one = "https://api.storyblok.com/v1/cdn/stories/health?version=published&token=wANpEQEsMYGOwLxwXQ76Ggtt"
    let two = "https://api.storyblok.com/v1/cdn/datasources/?token=wANpEQEsMYGOwLxwXQ76Ggtt"
    let three = "https://api.storyblok.com/v1/cdn/stories/vue?version=published&token=wANpEQEsMYGOwLxwXQ76Ggtt"

    const requestOne = axios.get(patientURLS.getPatient + `/${patientId}`);
    const requestTwo = axios.get(two);
    const requestThree = axios.get(three);

   await axios.all([requestOne, requestTwo, requestThree]).then(axios.spread((...responses) => {
        const responseOne = responses[0]
        const responseTwo = responses[1]
        const responesThree = responses[2]
        // use/access the results
    })).catch(errors => {
        // react on errors.
    })
}


export const postPatient = async (patient, setServerError, setAdded, e) => {

    setServerError("");
    setAdded(false)

    patient = frontToBackPatient(patient);
    try {
        const result = await api.post(patientURLS.postPatient, patient);


        if (result.status === 201) {
            setAdded(true)
            setTimeout(function () {
                setAdded(false)
            }, 5000);
        }

        e.target.reset();
    } catch (e) {
        console.log(e.response.data)

        //TODO handle validation errors
        setServerError(e.response.data);
    }

}

export const submitPatient = (patient) => {


    postPatient(patient).then(value => console.log('patient submitted'));
}


export const getPatientsList = async (setPatient, setTotalPages, pageNumber, itemsPerPage) => {
    try {
        const res = await api.get(`/patient/all?page=${pageNumber}&size=${itemsPerPage}`)
        setTotalPages(res.data.totalPages)
        setPatient(res.data.content)

        return res.data.content

    } catch (e) {
        console.log(e.response.data)
    }

}
