import React from 'react';
import "./pagination.css"
const Pagination = ({totalPages, paginate,current}) => {

    const pageNumbers = [];

    for (let i = 0; i < totalPages; i++) {
        pageNumbers.push(i);
    }
    const activePage ="page-item activePagination"
    const item =" page-item"

    return (
        <nav className={"nav"}>
            <ul className="pagination mt-3" aria-label="Page navigation example">
                {pageNumbers.map(pageNumber => (
                    <li key={pageNumber} className={current===pageNumber?activePage:item}>
                        <div onClick={() => paginate(pageNumber)} className="page-link">{pageNumber}</div>
                    </li>
                ))}
            </ul>
        </nav>
    );
};

export default Pagination;
