import React from 'react';
import {Link} from "react-router-dom";
import {getFullName, getPersonAge} from "../../Common Functions/PersonFunctions";

const PersonCard = ({person}) => {
    const icon=person.gender===true?'female':'male';
    return (
   <>
       {person === undefined ?  'no person' : <div className={'person-card-wrapper'}>
           <div className="icon-wrapper">
               <img src={process.env.PUBLIC_URL+`/assets/icons/${icon}.svg`} alt={`gender icon`}/>
           </div>
           <div className="main">
               <h2 className="full-name">{getFullName(person.personName)}</h2>
               <h5 className="mobile-number">{person.mobileNumber}</h5>
               <h5 className="last-visit">2 days ago</h5>
           </div>
           <div className="secondary">
               <h3 className="person-age">{getPersonAge(person.dateOfBirth)}</h3>
               <h3 className="village">{person.village}</h3>
               <Link className={'more'} to={'#'}>More</Link>

           </div>
       </div>};
   </> );
};

export default PersonCard;
