import api from '../../../Re-usable/Api Url/apiURL'
import {backToFrontPatient, frontToBackPatient} from "../../../../Common Functions/PersonFunctions";
import {useHistory} from "react-router";

export const getPatient = async (patientId) => {
    const result = await api.get(`/patient/${patientId}`);
    return backToFrontPatient(result);
}
const history = useHistory();


export const postPatient = async (patient,e)=>{


    const p=frontToBackPatient(patient);


    const result = await api.post(`/patient/`,p).then(
        ()=>{
            e.target.reset();
            history.goBack();
        }
    );

}

export const submitPatient=(patient)=>{



    postPatient(patient).then(value => console.log('patient submitted'));
}
