import {GET_ERRORS, SET_CURRENT_USER} from "../Types/Types";
import apiURL from "../../Components/Re-usable/Api Url/apiURL";
import jwt_decode from "jwt-decode";
import setJWTToken from "../../Components/User Management/Security/setJWTToken";


export const createNewUser = (newUser, history) => async dispatch => {
    try {
        await apiURL.post("/api/users/register", newUser);
        history.push("/login");
        dispatch({
            type: GET_ERRORS,
            payload: {}
        });
        console.log("User registration")
    } catch (err) {
        dispatch({
            type: GET_ERRORS,
            payload: err.response.data
        });
    }
};

export const ErrorHandler = async (error) => {


    console.log("catch requestWrapper")
    if (error.message === "Network Error") {
        console.log("****************")
        return null;

    }
}

export const login = LoginRequest => async dispatch => {
    try {
        // post => Login Request
        const res = await requestWrapper(apiURL.post("/api/users/login", LoginRequest))
        // extract token from res.data
        const {token} = res.data;
        // store the token in the localStorage
        localStorage.setItem("jwtToken", token);
        // set our token in header ***
        setJWTToken(token);
        // decode token on React
        const decoded = jwt_decode(token);
        console.log("decoded token", decoded)
        // dispatch to our securityReducer
        dispatch({
            type: SET_CURRENT_USER,
            payload: decoded
        });
    } catch (err) {

        // /*TODO backend not reached handle that here */
        // if(err.message==="Network Error") {
        //     console.log("****************")
        // }
        // else{
        //     console.log("server error",err.message)
        //     dispatch({
        //         type: GET_ERRORS,
        //         payload: err.response.data
        //     });
        // }

    }
};

export const logout = () => dispatch => {
    localStorage.removeItem("jwtToken");
    setJWTToken(false);
    dispatch({
        type: SET_CURRENT_USER,
        payload: {}
    });
};
