import React from 'react';
import {getFullName} from "../../../../Common Functions/PersonFunctions";

function PatientListTable({patients, rowClick, modifyPatientMethod}) {

    const PatientsListTableHead = [
        "Id",
        "Full Name",
        "Date Of Birth",
        "Mobile #",
        "Modify"
    ]

    return (


        <div className="patient-table">

            <table className="table">
                <thead>
                <tr>
                    {PatientsListTableHead.map(value => <th key={value} scope="col">{value}</th>)}
                </tr>
                </thead>
                <tbody>
                {patients === undefined ?
                    <tr>
                        <td colSpan="8"><p className="text-muted">No patients were found</p></td>
                    </tr>
                    :
                    patients.map(patient => {
                        return <tr key={patient.id}>
                            <td className="patientID" onClick={() => rowClick(patient.id)}>{patient.id}</td>
                            <td className="blue">{getFullName(patient.person.personName)}</td>
                            <td>{patient.person.dateOfBirth}</td>
                            <td>{patient.person.mobileNumber}</td>
                            <td><span>
                          <button onClick={() => modifyPatientMethod(patient.id)}><i
                              className="fas fa-edit"/></button>

                      </span></td>
                        </tr>
                    })}

                </tbody>
            </table>
            {/*<Pagination totalPages={totalPages} current={currentPage} paginate={paginate}/>*/}

        </div>

    );
}

export default PatientListTable;
