import React, {useLayoutEffect, useState} from 'react';


import {deletePatientFromWaitingQueue, getAllQueues} from "./Waiting Queue Logic/WaitingQueueFunctions";
import {invoicesURL} from "../../../Constants/URLConstants";
import {Link} from "react-router-dom";

function WaitingQueueList(props) {
      const QueueTableHead = [
        "Queue Number",
        "ID",
        "Patient Full Name",

        "Charge Patient",
        "Remove from Queue",

    ]

      const QueueTableBody = [

        "id",
        "patientFullName",


    ]
    const [waititngQueue, setWaititngQueue] = useState([])
    const [deleted, setDeleted] = useState("")

    const fetchData = async () => {

        setWaititngQueue(await getAllQueues())

    
    }

    useLayoutEffect(() => {
        fetchData()
    }, [])





