    setDeleted("Patient deleted")
    fetchData()

}else{

}
     }

    const chargePatientMethod= async (id) =>{
     const res=  await deletePatientFromWaitingQueue(id)


    }

    return <>
        {deleted}
        {(waititngQueue === undefined || Object.keys(waititngQueue).length === 0) ?
            <div className="component text-center">
                <h4>Add patients to the waiting Queue to view them</h4>
            </div>
            :
            Object.keys(waititngQueue).map((key) => {
                    if (waititngQueue[key].length !== 0) {

                        let value = waititngQueue[key];
                        return <div key={key} className="container justify-content-center component ">
                            <h1 className="text-center ">{key}</h1>

                            <hr/>
                            <table className="table">
                                <thead>
                                <tr>
                                    {QueueTableHead.map(head => <th key={head}>{head}</th>)}
                                </tr>
                                </thead>
                                <tbody>
                                {value.map((body, index) =>
                                    <tr key={body.id}>
                                        <td>{index + 1}</td>
                                        {QueueTableBody.map((item) => {
                                                return <td key={item}>
                                                    {body[item]}
                                                </td>
                                            }
                                        )}
                                        <td>
                                            <Link className="text-success font-weight-bold" to={{
                                                pathname: invoicesURL.invoiceForm + `/${body["patient"].id}`,
                                                state: {
                                                    patient: body["patientFullName"],
                                                    emplyeeId:body["employeeId"]
                                                }
                                            }}>Charge Patient</Link>
                                        </td>
                                        <td>
                                            <button className="btn-danger"
                                                    onClick={() => deletePatientFromQueueMethod(body.id)}>delete
                                            </button>
                                        </td>

                                    </tr>
                                )
                                }

                                </tbody>
                            </table>

                        </div>
                    } else {
                        delete waititngQueue[key]
                    }
                }
            )
        }
    </>;
}

export default WaitingQueueList;
