    //     setEmployee(responseOne.data)
    //     setInvoices(responseTwo.data)
    // })).catch(e => {
    //     console.log(e)
    // })
}
export const postEmployeetest = async (employee ) => {



    try {
        const result = await api.post(webApiURL.employee.postEmployee, employee);


    } catch (e) {
        console.log(e.response.data)


    }

}

export const postEmployee = async (employee, id, setServerError, setAdded, e) => {

    setServerError("");
    setAdded(false)

    employee = frontToBackEmployee(employee);
    if (id !== null) {
        employee = {...employee, id: id}
    }
    try {
        const result = await api.post(webApiURL.employee.postEmployee, employee);


        if (result.status === 201) {
            setAdded(true)
            setTimeout(function () {
                setAdded(false)
            }, 5000);
        }

        e.target.reset();
    } catch (e) {
        console.log(e.response.data)

        //TODO handle validation errors
        setServerError(e.response.data);
    }

}

export const submitEmployee = (employee) => {


    postEmployee(employee).then(value => console.log('employee submitted'));
}


export const getEmployeesList = async (setEmployee, setTotalPages, pageNumber, itemsPerPage) => {
    try {
        const res = await api.get(`/employee/all?page=${pageNumber}&size=${itemsPerPage}`)
        setTotalPages(res.data.totalPages)
        setEmployee(res.data.content)

        return res.data.content

    } catch (e) {
        console.log(e.response.data)
    }

}


export const checkEmployeeDept = (dueAmount) => {
    return dueAmount > 0 ? "hasDept" : ""
}

export const findEmployeesForm = async (name, setEmployeesList, setServerError) => {//method to get the employees list based on the person name if there is any error it is stored in the --> serverError
    console.log(name)
    try {
        const res = await api.get(webApiURL.employee.findEmployee + name)
        setEmployeesList(res.data)
    } catch (e) {
        if (e.response)
            setServerError(e.response.data)
        setEmployeesList([])
    }
}
