import React, {useLayoutEffect, useState} from 'react';

import PersonCard from "../../../Re-usable/PersonCard";
import {getEmployeesList} from "../Employee Logic/EmployeeFunctions";
import Pagination from "../../../Re-usable/Pagination/Pagination";
import {employeeURLS, patientURLS} from "../../../../Constants/URLConstants";
import {getAllClinics} from "../../Clinics Components/Clinics Functions/ClinicsFunctions";


function EmployeeListTable( ) {


    const [employee, setEmployee] = useState([])
    const [currentPage, setCurrentPage] = useState(0)
    const [totalPages, setTotalPages] = useState(1)
    const [itemsPerPage, setItemsPerPage] = useState(10)

    const paginate = (pageNumber) => {
        console.log('pagination called')
        setCurrentPage(pageNumber)
        const res=   getEmployeesList(  setTotalPages, pageNumber, itemsPerPage)
        setEmployee(res.content);
        setTotalPages(res.totalPages);
    }

    const fetchData = async () => {

        const res=await getEmployeesList(1,itemsPerPage);
        setEmployee(res.content);
        setTotalPages(res.totalPages);


    }

    useLayoutEffect(() => {
        fetchData()
    }, [])



    return (

        <div className=" employee-table cards-table-wrapper">

            {
               employee &&  employee.length > 0 ?
                    employee.map((employee, index) => {
                        return <PersonCard key={index} person={employee} infoPageUrl={employeeURLS.employeeInfo}/>
                    })

                    : "no employees found"
            }

            <Pagination totalPages={totalPages} current={currentPage} paginate={paginate}/>

        </div>

    );
}

export default EmployeeListTable;
